import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [judgesResponses, setJudgesResponses] = useState([]);
  const [loading, setLoading] = useState(true);

      <img 
        src={process.env.PUBLIC_URL + '/images/myImage.webp'} 
        alt="Top Image" 
        style={{ width: '100%', height: 'auto' }} 
      />

  // Example useEffect hook to simulate loading data
  useEffect(() => {
    // Simulate API call to get judge responses (in real case, use WebSockets or API request)
    setTimeout(() => {
      const dummyResponses = [
        { judge: 'Color Judge', feedback: 'The colors are complementary, well-suited to the theme.' },
        { judge: 'Creativity Judge', feedback: 'The outfit shows great creativity and originality.' },
        { judge: 'Technical Fashion Judge', feedback: 'The clothing choices match the formal style well.' },
      ];
      setJudgesResponses(dummyResponses);
      setLoading(false);
    }, 2000); // Simulating a 2-second API call
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="App">
      <h1>Dress to Impress - Judges' Feedback</h1>
      <div className="judge-container">
        {judgesResponses.map((response, index) => (
          <div key={index} className="judge-response">
            <h2>{response.judge}</h2>
            <p>{response.feedback}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
